/**
 * Netrunner - Color Definitions
 * Dark-mode native OKLCH color palette with RGB fallbacks
 * 
 * Usage:
 * - Import this file in your CSS or include in your build process
 * - Colors automatically fallback to RGB on unsupported browsers
 * - Use semantic color names for consistent theming
 */

:root {
  /* ===== PRIMARY COLORS (Electric Purple) ===== */
  --color-primary-50: #faf8ff;
  --color-primary-100: #f3f0ff;
  --color-primary-200: #e9e2ff;
  --color-primary-300: #d6c7ff;
  --color-primary-400: #b794f6;
  --color-primary-500: #8b5cf6;
  --color-primary-600: #7c3aed;
  --color-primary-700: #6d28d9;
  --color-primary-800: #5b21b6;
  --color-primary-900: #4c1d95;
  --color-primary-950: #2e1065;

  /* ===== ACCENT COLORS (Neo Pink) ===== */
  --color-accent-50: #fef7ff;
  --color-accent-100: #fdf2ff;
  --color-accent-200: #fce7ff;
  --color-accent-300: #f8ccff;
  --color-accent-400: #f472b6;
  --color-accent-500: #ec4899;
  --color-accent-600: #db2777;
  --color-accent-700: #be185d;
  --color-accent-800: #9d174d;
  --color-accent-900: #831843;
  --color-accent-950: #500724;

  /* ===== SEMANTIC COLORS ===== */
  /* Success (Green) */
  --color-success-50: #f0fdf5;
  --color-success-100: #dcfce8;
  --color-success-200: #bbf7d1;
  --color-success-300: #86efac;
  --color-success-400: #4ade80;
  --color-success-500: #22c55e;
  --color-success-600: #16a34a;
  --color-success-700: #15803d;
  --color-success-800: #166534;
  --color-success-900: #14532d;

  /* Warning (Amber) */
  --color-warning-50: #fffbeb;
  --color-warning-100: #fef3c7;
  --color-warning-200: #fde68a;
  --color-warning-300: #fcd34d;
  --color-warning-400: #fbbf24;
  --color-warning-500: #f59e0b;
  --color-warning-600: #d97706;
  --color-warning-700: #b45309;
  --color-warning-800: #92400e;
  --color-warning-900: #78350f;

  /* Danger (Red) */
  --color-danger-50: #fef2f2;
  --color-danger-100: #fee2e2;
  --color-danger-200: #fecaca;
  --color-danger-300: #fca5a5;
  --color-danger-400: #f87171;
  --color-danger-500: #ef4444;
  --color-danger-600: #dc2626;
  --color-danger-700: #b91c1c;
  --color-danger-800: #991b1b;
  --color-danger-900: #7f1d1d;

  /* Info (Blue) */
  --color-info-50: #eff6ff;
  --color-info-100: #dbeafe;
  --color-info-200: #bfdbfe;
  --color-info-300: #93c5fd;
  --color-info-400: #60a5fa;
  --color-info-500: #3b82f6;
  --color-info-600: #2563eb;
  --color-info-700: #1d4ed8;
  --color-info-800: #1e40af;
  --color-info-900: #1e3a8a;

  /* ===== NEUTRALS (Subtly tinted) ===== */
  --color-neutral-50: #fafafa;
  --color-neutral-100: #f5f5f5;
  --color-neutral-200: #e5e5e5;
  --color-neutral-300: #d4d4d4;
  --color-neutral-400: #a3a3a3;
  --color-neutral-500: #737373;
  --color-neutral-600: #525252;
  --color-neutral-700: #404040;
  --color-neutral-800: #262626;
  --color-neutral-900: #171717;
  --color-neutral-950: #0a0a0a;

  /* ===== RETROWAVE ACCENT COLORS (Use sparingly) ===== */
  --color-retro-solar-flare: #ff901f;     /* Premium highlights */
  --color-retro-cosmic-punch: #ff2975;    /* Special CTAs */
  --color-retro-ultraviolet: #f222ff;     /* Marketing gradients */
  --color-retro-quantum-grape: #8c1eff;   /* Alternative primary */
  --color-retro-hypersonic-rush: #1b51ff; /* Alternative info */
  --color-retro-electric-surge: #00eaff;  /* Loading states */
  --color-retro-neon-drift: #0aff99;      /* Success celebrations */
  --color-retro-midnight-vortex: #120458; /* Deep backgrounds */
  --color-retro-quantum-storm: #2c1a94;   /* Alternative surfaces */

  /* ===== SEMANTIC ALIASES ===== */
  --color-background: var(--color-neutral-950);
  --color-foreground: var(--color-neutral-50);
  --color-muted: var(--color-neutral-500);
  --color-muted-foreground: var(--color-neutral-400);
  --color-border: var(--color-neutral-800);
  --color-input: var(--color-neutral-900);
  --color-ring: var(--color-primary-500);
}

/* ===== OKLCH COLOR DEFINITIONS (Modern browsers) ===== */
@supports (color: oklch(0 0 0)) {
  :root {
    /* Primary Colors (Electric Purple - hue 285Ã‚Â°) */
    --color-primary-50: oklch(0.98 0.02 285);
    --color-primary-100: oklch(0.95 0.05 285);
    --color-primary-200: oklch(0.90 0.08 285);
    --color-primary-300: oklch(0.82 0.12 285);
    --color-primary-400: oklch(0.70 0.15 285);
    --color-primary-500: oklch(0.58 0.20 285);
    --color-primary-600: oklch(0.45 0.18 285);
    --color-primary-700: oklch(0.35 0.15 285);
    --color-primary-800: oklch(0.25 0.12 285);
    --color-primary-900: oklch(0.15 0.08 285);
    --color-primary-950: oklch(0.09 0.05 285);

    /* Accent Colors (Neo Pink - hue 320Ã‚Â°) */
    --color-accent-50: oklch(0.98 0.02 320);
    --color-accent-100: oklch(0.95 0.05 320);
    --color-accent-200: oklch(0.90 0.10 320);
    --color-accent-300: oklch(0.82 0.18 320);
    --color-accent-400: oklch(0.70 0.25 320);
    --color-accent-500: oklch(0.58 0.28 320);
    --color-accent-600: oklch(0.45 0.25 320);
    --color-accent-700: oklch(0.35 0.20 320);
    --color-accent-800: oklch(0.25 0.15 320);
    --color-accent-900: oklch(0.15 0.10 320);
    --color-accent-950: oklch(0.09 0.06 320);

    /* Semantic Colors */
    --color-success-400: oklch(0.75 0.15 145);
    --color-success-500: oklch(0.65 0.18 145);
    --color-success-600: oklch(0.50 0.15 145);

    --color-warning-400: oklch(0.80 0.12 85);
    --color-warning-500: oklch(0.70 0.15 85);
    --color-warning-600: oklch(0.55 0.12 85);

    --color-danger-400: oklch(0.70 0.20 25);
    --color-danger-500: oklch(0.60 0.22 25);
    --color-danger-600: oklch(0.45 0.18 25);

    --color-info-400: oklch(0.72 0.15 220);
    --color-info-500: oklch(0.60 0.18 220);
    --color-info-600: oklch(0.45 0.15 220);

    /* Neutrals with subtle purple tint */
    --color-neutral-50: oklch(0.98 0.01 285);
    --color-neutral-100: oklch(0.95 0.015 285);
    --color-neutral-200: oklch(0.90 0.02 285);
    --color-neutral-300: oklch(0.82 0.025 285);
    --color-neutral-400: oklch(0.70 0.03 285);
    --color-neutral-500: oklch(0.58 0.035 285);
    --color-neutral-600: oklch(0.45 0.04 285);
    --color-neutral-700: oklch(0.35 0.045 285);
    --color-neutral-800: oklch(0.25 0.05 285);
    --color-neutral-900: oklch(0.15 0.055 285);
    --color-neutral-950: oklch(0.09 0.06 285);

    /* RetroWave Accent Colors (OKLCH) */
    --color-retro-solar-flare: oklch(0.758 0.173 58);
    --color-retro-cosmic-punch: oklch(0.654 0.243 8);
    --color-retro-ultraviolet: oklch(0.69 0.308 325);
    --color-retro-quantum-grape: oklch(0.553 0.285 297);
    --color-retro-hypersonic-rush: oklch(0.53 0.261 265);
    --color-retro-electric-surge: oklch(0.856 0.147 206);
    --color-retro-neon-drift: oklch(0.879 0.213 156);
    --color-retro-midnight-vortex: oklch(0.225 0.134 275);
    --color-retro-quantum-storm: oklch(0.344 0.183 277);
  }
}

/* ===== COMPONENT-SPECIFIC COLOR UTILITIES ===== */
.color-scheme-neo-synthwave {
  color-scheme: dark;
  --color-primary: var(--color-primary-500);
  --color-primary-hover: var(--color-primary-400);
  --color-primary-active: var(--color-primary-600);

  --color-secondary: var(--color-accent-500);
  --color-secondary-hover: var(--color-accent-400);
  --color-secondary-active: var(--color-accent-600);

  --color-surface: var(--color-neutral-900);
  --color-surface-elevated: var(--color-neutral-800);
  --color-surface-overlay: var(--color-neutral-700);
}

/* ===== GRADIENT UTILITIES ===== */
.gradient-primary {
  background: linear-gradient(135deg, 
    var(--color-primary-500), 
    var(--color-accent-500)
  );
}

.gradient-retro-special {
  background: linear-gradient(135deg,
    var(--color-retro-ultraviolet),
    var(--color-retro-cosmic-punch),
    var(--color-retro-electric-surge)
  );
}

.gradient-surface {
  background: linear-gradient(135deg, 
    var(--color-neutral-950), 
    var(--color-neutral-900)
  );
}

/* ===== USAGE EXAMPLES ===== */
/*
Primary Button:
  background: var(--color-primary-500);
  color: white;

Primary Button Hover:
  background: var(--color-primary-400);

Success Status:
  background: var(--color-success-500);
  color: white;

Special Premium CTA:
  background: var(--color-retro-cosmic-punch);
  color: white;

Dark Surface:
  background: var(--color-neutral-900);
  border: 1px solid var(--color-neutral-800);
*/

attached_assets/typography_1759070852456.content:
/**
 * Netrunner Typography System
 * Enhanced Satoshi + Fira Code with fluid scaling and creative hierarchy
 * 
 * Dependencies:
 * - Satoshi font family (300-900 weights)
 * - Fira Code font family (300-600 weights)
 * 
 * Import URLs:
 * @import url('https://api.fontshare.com/v2/css?f[]=satoshi@300,400,500,600,700,800,900&display=swap');
 * @import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@300;400;500;600&display=swap');
 */

:root {
  /* ===== FONT FAMILIES ===== */
  --font-family-sans: 'Satoshi', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
  --font-family-mono: 'Fira Code', 'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace;

  /* ===== TYPE SCALE (Major Third - 1.25 ratio) ===== */
  --type-scale-ratio: 1.25;

  /* Base font sizes with fluid scaling (16px mobile â†’ 18px desktop) */
  --font-size-base: clamp(1rem, 0.875rem + 0.5vw, 1.125rem);

  /* Mathematical scale progression */
  --font-size-xs: calc(var(--font-size-base) / var(--type-scale-ratio) / var(--type-scale-ratio));  /* ~0.64rem */
  --font-size-sm: calc(var(--font-size-base) / var(--type-scale-ratio));                           /* ~0.8rem */
  --font-size-base: var(--font-size-base);                                                         /* 1rem - 1.125rem */
  --font-size-lg: calc(var(--font-size-base) * var(--type-scale-ratio));                          /* ~1.25rem */
  --font-size-xl: calc(var(--font-size-base) * var(--type-scale-ratio) * var(--type-scale-ratio)); /* ~1.56rem */
  --font-size-2xl: calc(var(--font-size-xl) * var(--type-scale-ratio));                          /* ~1.95rem */
  --font-size-3xl: calc(var(--font-size-2xl) * var(--type-scale-ratio));                         /* ~2.44rem */
  --font-size-4xl: calc(var(--font-size-3xl) * var(--type-scale-ratio));                         /* ~3.05rem */

  /* Enhanced display sizes for hero/marketing moments */
  --font-size-display-sm: clamp(2.5rem, 4vw, 3.5rem);
  --font-size-display-md: clamp(3rem, 5vw, 4.5rem);
  --font-size-display-lg: clamp(3.5rem, 6vw, 6rem);
  --font-size-display-xl: clamp(4rem, 8vw, 8rem);

  /* ===== FONT WEIGHTS ===== */
  --font-weight-light: 300;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-semibold: 600;
  --font-weight-bold: 700;
  --font-weight-extrabold: 800;
  --font-weight-black: 900;

  /* ===== LINE HEIGHTS ===== */
  --line-height-tight: 1.1;    /* Display text, large headings */
  --line-height-snug: 1.2;     /* Headings */
  --line-height-normal: 1.4;   /* UI text, small text */
  --line-height-relaxed: 1.6;  /* Body text, optimal reading */
  --line-height-loose: 1.8;    /* Long-form content */

  /* ===== LETTER SPACING ===== */
  --letter-spacing-tighter: -0.05em;  /* Very large display text */
  --letter-spacing-tight: -0.025em;   /* Large headings */
  --letter-spacing-normal: 0em;       /* Default */
  --letter-spacing-wide: 0.025em;     /* Small text, labels */
  --letter-spacing-wider: 0.05em;     /* Buttons, badges */
  --letter-spacing-widest: 0.1em;     /* All caps, metadata */

  /* ===== SEMANTIC TEXT STYLES ===== */
  /* Pre-composed font shorthand values for consistent application */
  --text-display-xl: var(--font-weight-black) var(--font-size-display-xl)/var(--line-height-tight) var(--font-family-sans);
  --text-display-lg: var(--font-weight-extrabold) var(--font-size-display-lg)/var(--line-height-tight) var(--font-family-sans);
  --text-display-md: var(--font-weight-extrabold) var(--font-size-display-md)/var(--line-height-tight) var(--font-family-sans);
  --text-display-sm: var(--font-weight-bold) var(--font-size-display-sm)/var(--line-height-snug) var(--font-family-sans);

  --text-heading-4xl: var(--font-weight-extrabold) var(--font-size-4xl)/var(--line-height-tight) var(--font-family-sans);
  --text-heading-3xl: var(--font-weight-bold) var(--font-size-3xl)/var(--line-height-tight) var(--font-family-sans);
  --text-heading-2xl: var(--font-weight-bold) var(--font-size-2xl)/var(--line-height-snug) var(--font-family-sans);
  --text-heading-xl: var(--font-weight-semibold) var(--font-size-xl)/var(--line-height-snug) var(--font-family-sans);
  --text-heading-lg: var(--font-weight-medium) var(--font-size-lg)/var(--line-height-normal) var(--font-family-sans);

  --text-body-lg: var(--font-weight-medium) var(--font-size-lg)/var(--line-height-relaxed) var(--font-family-sans);
  --text-body-base: var(--font-weight-normal) var(--font-size-base)/var(--line-height-relaxed) var(--font-family-sans);
  --text-body-sm: var(--font-weight-normal) var(--font-size-sm)/var(--line-height-normal) var(--font-family-sans);
  --text-body-xs: var(--font-weight-normal) var(--font-size-xs)/var(--line-height-normal) var(--font-family-sans);
}

/* ===== BASE TYPOGRAPHY CLASSES ===== */

/* Display Text - Hero sections, marketing moments */
.text-display-xl {
  font: var(--text-display-xl);
  letter-spacing: var(--letter-spacing-tighter);
}

.text-display-lg {
  font: var(--text-display-lg);
  letter-spacing: var(--letter-spacing-tighter);
}

.text-display-md {
  font: var(--text-display-md);
  letter-spacing: var(--letter-spacing-tight);
}

.text-display-sm {
  font: var(--text-display-sm);
  letter-spacing: var(--letter-spacing-tight);
}

/* Heading Hierarchy */
.text-4xl {
  font: var(--text-heading-4xl);
  letter-spacing: var(--letter-spacing-tight);
}

.text-3xl {
  font: var(--text-heading-3xl);
  letter-spacing: var(--letter-spacing-tight);
}

.text-2xl {
  font: var(--text-heading-2xl);
  letter-spacing: var(--letter-spacing-normal);
}

.text-xl {
  font: var(--text-heading-xl);
  letter-spacing: var(--letter-spacing-normal);
}

.text-lg {
  font: var(--text-heading-lg);
  letter-spacing: var(--letter-spacing-normal);
}

/* Body Text */
.text-base {
  font: var(--text-body-base);
}

.text-sm {
  font: var(--text-body-sm);
}

.text-xs {
  font: var(--text-body-xs);
  letter-spacing: var(--letter-spacing-wide);
}

/* Emphasis variants */
.text-lg-emphasis {
  font: var(--text-body-lg);
}

/* Monospace Typography */
.text-mono {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1; /* Enable programming ligatures */
  font-size: 0.9em; /* Slightly smaller than surrounding text */
}

.text-mono-lg {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1;
  font-size: var(--font-size-lg);
  font-weight: var(--font-weight-medium);
}

.text-mono-sm {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1;
  font-size: var(--font-size-sm);
}

.text-mono-xs {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1;
  font-size: var(--font-size-xs);
}

/* ===== FONT WEIGHT UTILITIES ===== */
.font-light { font-weight: var(--font-weight-light); }
.font-normal { font-weight: var(--font-weight-normal); }
.font-medium { font-weight: var(--font-weight-medium); }
.font-semibold { font-weight: var(--font-weight-semibold); }
.font-bold { font-weight: var(--font-weight-bold); }
.font-extrabold { font-weight: var(--font-weight-extrabold); }
.font-black { font-weight: var(--font-weight-black); }

/* ===== LETTER SPACING UTILITIES ===== */
.tracking-tighter { letter-spacing: var(--letter-spacing-tighter); }
.tracking-tight { letter-spacing: var(--letter-spacing-tight); }
.tracking-normal { letter-spacing: var(--letter-spacing-normal); }
.tracking-wide { letter-spacing: var(--letter-spacing-wide); }
.tracking-wider { letter-spacing: var(--letter-spacing-wider); }
.tracking-widest { letter-spacing: var(--letter-spacing-widest); }

/* ===== LINE HEIGHT UTILITIES ===== */
.leading-tight { line-height: var(--line-height-tight); }
.leading-snug { line-height: var(--line-height-snug); }
.leading-normal { line-height: var(--line-height-normal); }
.leading-relaxed { line-height: var(--line-height-relaxed); }
.leading-loose { line-height: var(--line-height-loose); }

/* ===== SEMANTIC TYPOGRAPHY COMPONENTS ===== */

/* Code blocks */
.code-block {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1;
  font-size: var(--font-size-sm);
  line-height: var(--line-height-relaxed);
}

/* Inline code */
.code-inline {
  font-family: var(--font-family-mono);
  font-feature-settings: 'liga' 1, 'calt' 1;
  font-size: 0.875em;
  padding: 0.125rem 0.25rem;
  border-radius: 0.25rem;
}

/* Labels and metadata */
.text-label {
  font-family: var(--font-family-sans);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
  letter-spacing: var(--letter-spacing-widest);
  text-transform: uppercase;
}

/* Button text */
.text-button {
  font-family: var(--font-family-sans);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-semibold);
  letter-spacing: var(--letter-spacing-wide);
  line-height: 1;
}

.text-button-lg {
  font-family: var(--font-family-sans);
  font-size: var(--font-size-base);
  font-weight: var(--font-weight-semibold);
  letter-spacing: var(--letter-spacing-wide);
  line-height: 1;
}

/* Navigation text */
.text-nav {
  font-family: var(--font-family-sans);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);
  letter-spacing: var(--letter-spacing-normal);
  line-height: var(--line-height-normal);
}

/* ===== RESPONSIVE TYPOGRAPHY UTILITIES ===== */

/* Responsive display text - scales more aggressively on larger screens */
.text-display-responsive {
  font-family: var(--font-family-sans);
  font-weight: var(--font-weight-extrabold);
  font-size: clamp(2rem, 6vw, 6rem);
  line-height: var(--line-height-tight);
  letter-spacing: var(--letter-spacing-tight);
}

/* Responsive heading - moderate scaling */
.text-heading-responsive {
  font-family: var(--font-family-sans);
  font-weight: var(--font-weight-bold);
  font-size: clamp(1.5rem, 3vw, 2.5rem);
  line-height: var(--line-height-snug);
  letter-spacing: var(--letter-spacing-normal);
}

/* ===== ACCESSIBILITY & PERFORMANCE ===== */

/* Ensure proper font loading */
.font-loading-optimal {
  font-display: swap;
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    font-variation-settings: normal !important;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --letter-spacing-tight: 0em;
    --letter-spacing-tighter: 0em;
  }

  .text-display-xl,
  .text-display-lg,
  .text-display-md,
  .text-4xl,
  .text-3xl {
    letter-spacing: var(--letter-spacing-normal);
  }
}

/* ===== USAGE EXAMPLES IN COMMENTS ===== */

/*
USAGE EXAMPLES:

Hero Section:
<h1 class="text-display-lg">Ship Creative Code</h1>
<p class="text-lg-emphasis">Professional developer tools with a creative edge</p>

Page Structure:
<h1 class="text-4xl">Documentation</h1>
<h2 class="text-2xl">Getting Started</h2>
<h3 class="text-xl">Installation</h3>

Body Content:
<p class="text-base">This is the optimal reading size for body text...</p>
<p class="text-sm">Secondary information or captions</p>
<span class="text-xs text-label">Metadata â€¢ Published 2 days ago</span>

Code Examples:
<pre class="code-block">const config = { theme: 'neo-synthwave' };</pre>
<code class="code-inline">npm install</code>

Buttons:
<button class="text-button">Primary Action</button>
<button class="text-button-lg">Hero CTA</button>

Navigation:
<nav class="text-nav">
  <a href="#">Projects</a>
  <a href="#">Documentation</a>
</nav>
*/

