@import url('https://api.fontshare.com/v2/css?f[]=satoshi@300,400,500,600,700,800,900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@300;400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Cyberpunk Color System */
  --background: hsl(240, 30%, 4%);
  --foreground: hsl(240, 10%, 96%);
  --card: hsl(240, 20%, 6%);
  --card-foreground: hsl(240, 10%, 90%);
  --popover: hsl(240, 20%, 8%);
  --popover-foreground: hsl(240, 10%, 95%);
  --primary: hsl(260, 100%, 80%);
  --primary-foreground: hsl(240, 100%, 10%);
  --secondary: hsl(320, 80%, 60%);
  --secondary-foreground: hsl(320, 100%, 10%);
  --muted: hsl(240, 15%, 35%);
  --muted-foreground: hsl(240, 10%, 65%);
  --accent: hsl(320, 100%, 60%);
  --accent-foreground: hsl(320, 100%, 10%);
  --destructive: hsl(0, 62%, 60%);
  --destructive-foreground: hsl(0, 85%, 95%);
  --border: hsl(240, 20%, 15%);
  --input: hsl(240, 20%, 8%);
  --input-foreground: hsl(240, 10%, 85%);
  --ring: hsl(260, 100%, 70%);
  --chart-1: hsl(260, 100%, 80%);
  --chart-2: hsl(180, 100%, 60%);
  --chart-3: hsl(58, 100%, 70%);
  --chart-4: hsl(320, 100%, 60%);
  --chart-5: hsl(285, 100%, 60%);
  --sidebar: hsl(240, 20%, 6%);
  --sidebar-foreground: hsl(240, 10%, 90%);
  --sidebar-primary: hsl(260, 100%, 80%);
  --sidebar-primary-foreground: hsl(240, 100%, 10%);
  --sidebar-accent: hsl(320, 100%, 60%);
  --sidebar-accent-foreground: hsl(320, 100%, 10%);
  --sidebar-border: hsl(240, 20%, 15%);
  --sidebar-ring: hsl(260, 100%, 70%);

  /* Typography */
  --font-sans: 'Satoshi', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Fira Code', 'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace;
  --radius: 0.5rem;

  /* Cyberpunk specific colors */
  --neon-cyan: hsl(180, 100%, 60%);
  --neon-pink: hsl(320, 100%, 70%);
  --neon-yellow: hsl(58, 100%, 70%);
  --retro-purple: hsl(285, 100%, 60%);

  /* Shadows */
  --shadow-2xs: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1);
  --shadow-xs: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1);
  --shadow-sm: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1), 0px 1px 2px -1px hsl(260, 100%, 70%, 0.1);
  --shadow: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1), 0px 1px 2px -1px hsl(260, 100%, 70%, 0.1);
  --shadow-md: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1), 0px 2px 4px -1px hsl(260, 100%, 70%, 0.1);
  --shadow-lg: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1), 0px 4px 6px -1px hsl(260, 100%, 70%, 0.1);
  --shadow-xl: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1), 0px 8px 10px -1px hsl(260, 100%, 70%, 0.1);
  --shadow-2xl: 0px 2px 0px 0px hsl(260, 100%, 70%, 0.1);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Type scale from typography.css */
  --type-scale-ratio: 1.25;
  --font-size-base: clamp(1rem, 0.875rem + 0.5vw, 1.125rem);
  --font-size-xs: calc(var(--font-size-base) / var(--type-scale-ratio) / var(--type-scale-ratio));
  --font-size-sm: calc(var(--font-size-base) / var(--type-scale-ratio));
  --font-size-lg: calc(var(--font-size-base) * var(--type-scale-ratio));
  --font-size-xl: calc(var(--font-size-base) * var(--type-scale-ratio) * var(--type-scale-ratio));
  --font-size-2xl: calc(var(--font-size-xl) * var(--type-scale-ratio));
  --font-size-3xl: calc(var(--font-size-2xl) * var(--type-scale-ratio));
  --font-size-4xl: calc(var(--font-size-3xl) * var(--type-scale-ratio));

  /* Font weights */
  --font-weight-light: 300;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-semibold: 600;
  --font-weight-bold: 700;
  --font-weight-extrabold: 800;
  --font-weight-black: 900;

  /* Line heights */
  --line-height-tight: 1.1;
  --line-height-snug: 1.2;
  --line-height-normal: 1.4;
  --line-height-relaxed: 1.6;
  --line-height-loose: 1.8;

  /* Letter spacing */
  --letter-spacing-tighter: -0.05em;
  --letter-spacing-tight: -0.025em;
  --letter-spacing-normal: 0em;
  --letter-spacing-wide: 0.025em;
  --letter-spacing-wider: 0.05em;
  --letter-spacing-widest: 0.1em;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  .mono { 
    font-family: var(--font-mono);
    font-feature-settings: 'liga' 1, 'calt' 1;
  }
}

@layer components {
  .glow-primary {
    box-shadow: 0 0 20px hsla(260, 100%, 70%, 0.3);
  }

  .glow-accent {
    box-shadow: 0 0 20px hsla(320, 100%, 60%, 0.3);
  }

  .glow-cyan {
    box-shadow: 0 0 20px hsla(180, 100%, 60%, 0.3);
  }

  .glow-yellow {
    box-shadow: 0 0 15px hsla(58, 100%, 70%, 0.4);
  }

  .text-glow {
    text-shadow: 0 0 10px currentColor;
  }

  .gradient-primary {
    background: linear-gradient(135deg, var(--primary), var(--accent));
  }

  .border-gradient {
    background: linear-gradient(135deg, var(--primary), var(--accent)) padding-box,
                linear-gradient(135deg, var(--primary), var(--accent)) border-box;
    border: 1px solid transparent;
  }

  .cyber-grid {
    background-image: 
      linear-gradient(rgba(134, 116, 246, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(134, 116, 246, 0.1) 1px, transparent 1px);
    background-size: 20px 20px;
  }

  .hp-bar::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    background: linear-gradient(90deg, #ff2975, #ff901f);
    width: var(--hp-width, 85%);
    border-radius: inherit;
  }

  .xp-bar::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    background: linear-gradient(90deg, var(--primary), var(--neon-cyan));
    width: var(--xp-width, 65%);
    border-radius: inherit;
  }

  /* Typography classes from the provided system */
  .text-display-xl {
    font: var(--font-weight-black) var(--font-size-4xl)/var(--line-height-tight) var(--font-sans);
    letter-spacing: var(--letter-spacing-tighter);
  }

  .text-display-lg {
    font: var(--font-weight-extrabold) var(--font-size-3xl)/var(--line-height-tight) var(--font-sans);
    letter-spacing: var(--letter-spacing-tighter);
  }

  .text-display-md {
    font: var(--font-weight-extrabold) var(--font-size-2xl)/var(--line-height-tight) var(--font-sans);
    letter-spacing: var(--letter-spacing-tight);
  }

  .text-display-sm {
    font: var(--font-weight-bold) var(--font-size-xl)/var(--line-height-snug) var(--font-sans);
    letter-spacing: var(--letter-spacing-tight);
  }

  .text-button {
    font-family: var(--font-sans);
    font-size: var(--font-size-sm);
    font-weight: var(--font-weight-semibold);
    letter-spacing: var(--letter-spacing-wide);
    line-height: 1;
  }

  .text-button-lg {
    font-family: var(--font-sans);
    font-size: var(--font-size-base);
    font-weight: var(--font-weight-semibold);
    letter-spacing: var(--letter-spacing-wide);
    line-height: 1;
  }

  .text-label {
    font-family: var(--font-sans);
    font-size: var(--font-size-xs);
    font-weight: var(--font-weight-medium);
    letter-spacing: var(--letter-spacing-widest);
    text-transform: uppercase;
  }
}

@layer utilities {
  .animate-pulse-neon {
    animation: pulse-neon 2s infinite;
  }

  @keyframes pulse-neon {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }
}

